cls
Remove-Variable * -ErrorAction SilentlyContinue

#region Supression de la console powershell, A COMMENTER (DESACTIVER) SI ON VEUT LA CONSOLE POWERSHELL EN ARRIERE PLAN

$t = '[DllImport("user32.dll")] public static extern bool ShowWindow(int handle, int state);'
add-type -name win -member $t -namespace native
[native.win]::ShowWindow(([System.Diagnostics.Process]::GetCurrentProcess() | Get-Process).MainWindowHandle, 0)

#endregion

#region Variables

# Chargement des assemblies
Add-Type -AssemblyName PresentationFramework,PresentationCore,WindowsBase,System.Windows.Forms

$imgIconApp = ""
$imgChronoMiddleApp = ""

$msgAProposDe = @"
PtitSyno37

PtitChrono est conçu par PtitSyno37, un domaine géré par l'incroyable FabiOus37 !

N'hesitez pas à me contacter : fabien.petit@ptitsyno37.fr

@+ Mamène !
"@

$cheminExecution = (Get-Location).path
$timer = New-Object System.Windows.Forms.Timer   #Création de l'objet Timer
$timer.Interval = 1000    #Intervale d'execution des actions régler à 1s

#endregion

#region Definition de l'interface graphique

[xml]$xaml = @"
<Window 
        xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
        xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
        Title="PtitChrono" Height="363" Width="263" Background="White" WindowStartupLocation="CenterScreen" IsHitTestVisible="True" IsTabStop="True" ResizeMode="NoResize" WindowStyle="SingleBorderWindow">
    <Window.TaskbarItemInfo>
         <TaskbarItemInfo/>
    </Window.TaskbarItemInfo>
    <Grid>
        <Button x:Name="Demarrer" Content="Demarrer" Margin="10,0,134,10" Height="29" VerticalAlignment="Bottom"/>
        <Button x:Name="Arreter" Content="Arrêter" Margin="135,0,10,10" Height="29" VerticalAlignment="Bottom"/>
        <Image x:Name="image" Margin="78,32,77,0" Height="105" VerticalAlignment="Top">
        </Image>
        <ListBox x:Name="numCompteur" Height="26" Margin="84,158,84,0" VerticalAlignment="Top" RenderTransformOrigin="0.5,0.5"/>
        <ListBox x:Name="numCompteur_Copy" Height="26" Margin="25,239,177,0" VerticalAlignment="Top" RenderTransformOrigin="0.5,0.5"/>
        <ListBox x:Name="numCompteur_Copy1" Height="26" Margin="96,239,106,0" VerticalAlignment="Top" RenderTransformOrigin="0.5,0.5"/>
        <ListBox x:Name="numCompteur_Copy2" Height="26" Margin="168,239,34,0" VerticalAlignment="Top" RenderTransformOrigin="0.5,0.5"/>
        <GroupBox x:Name="numConvertisseur" Header="Convertisseur" Margin="10,199,10,49" FontStyle="Italic"/>
        <Label x:Name="label" Content="Timer (en secondes)" HorizontalAlignment="Left" Margin="66,132,0,0" VerticalAlignment="Top"/>
        <StackPanel VerticalAlignment="Top">
            <Menu Width="Auto" Height="20" Background="White">
                <MenuItem Header="Fichier">
                    <MenuItem x:Name="Quitter" Header="Quitter" HorizontalAlignment="Left" Width="140"/>
                </MenuItem>
                <MenuItem Header="Aide">
                    <MenuItem x:Name="About" Header="À propos de PtitSyno37" HorizontalAlignment="Left" Width="213" Margin="1,0,0,0"/>
                </MenuItem>
            </Menu>
        </StackPanel>
        <Label x:Name="label_Copy" Content="Heure(s)" HorizontalAlignment="Left" Margin="19,217,0,0" VerticalAlignment="Top"/>
        <Label x:Name="label_Copy1" Content="Minute(s)" HorizontalAlignment="Left" Margin="90,217,0,0" VerticalAlignment="Top"/>
        <Label x:Name="label_Copy2" Content="Seconde(s)" HorizontalAlignment="Left" Margin="162,217,0,0" VerticalAlignment="Top"/>
    </Grid>
</Window>
"@

#endregion

#region Chargement de l'interface et des événements

#Chargement des Interfaces
$wpf=(New-Object System.Xml.XmlNodeReader $xaml)
$Form=[Windows.Markup.XamlReader]::Load($wpf)

#Récupération des contrôles WPF dans PowerShell
$numCompteur = $Form.FindName('numCompteur')
$demarrer = $Form.FindName('Demarrer')
$arreter = $Form.FindName('Arreter')
$convHeures = $Form.FindName('numCompteur_Copy')
$convMinutes = $Form.FindName('numCompteur_Copy1')
$convSecondes = $Form.FindName('numCompteur_Copy2')
$menuAProproDe = $Form.FindName('About')
$menuQuitter = $Form.FindName('Quitter')
$appImgChrono = $Form.FindName('image')

$demarrerDefault = $demarrer.Content   #Récupération de la valeur par défaut du bouton "Démarrer"
#endregion

#region Traitement

#region Streaming des images en base64, permet d'eviter de rattacher les images au script
$bitmapIcon = New-Object System.Windows.Media.Imaging.BitmapImage
$bitmapIcon.BeginInit()
$bitmapIcon.StreamSource = [System.IO.MemoryStream][System.Convert]::FromBase64String($imgIconApp)
$bitmapIcon.EndInit()
$bitmapIcon.Freeze()   #Empêche la fuite de mémoire

$bitmapChrono = New-Object System.Windows.Media.Imaging.BitmapImage
$bitmapChrono.BeginInit()
$bitmapChrono.StreamSource = [System.IO.MemoryStream][System.Convert]::FromBase64String($imgChronoMiddleApp)
$bitmapChrono.EndInit()
$bitmapChrono.Freeze()
#endregion

#Attribution des images à leur form
$Form.Icon = $bitmapIcon
$Form.TaskbarItemInfo.Overlay = $bitmapIcon
$Form.TaskbarItemInfo.Description = $Form.Title
$appImgChrono.Source = $bitmapChrono

#Désativation des champs "Convertisseur" et "Seconde(s)"
$convHeures.IsEnabled = $false
$convMinutes.IsEnabled = $false
$convSecondes.IsEnabled = $false
$numCompteur.IsEnabled = $false
$arreter.IsEnabled = $false


#Evenement lorsque que le bouton Demarrer est executé
$demarrer.Add_Click({
    if ($demarrer.Content -eq $demarrerDefault){
        $demarrer.content = "Pause"    # Changement de nom du bouton lorsque que la valeur par défaut est défini

        # RAZ des compteurs
        $numCompteur.Items.clear()
        $convHeures.Items.clear()
        $convMinutes.Items.clear()
        $convSecondes.Items.clear()

        $convHeures.AddText(0)
        $convMinutes.AddText(0)
        $numCompteur.AddText(0)
        $convSecondes.AddText(0)

        $arreter.IsEnabled = $true

        $timer.start()
    } elseif ($timer.Enabled){
        $timer.stop()
        $demarrer.Content = "Reprendre"
    } else {
        $timer.start()
        $demarrer.Content = "Pause"
    }
})

#Evenement lorsque le bouton Arreter est executé
$arreter.Add_Click({
    # Arrêt du timer
    $timer.stop()
    $demarrer.content = $demarrerDefault
    $arreter.IsEnabled = $false
})

#Action du menu contextuel "Quitter"
$menuQuitter.Add_Click({
    $timer.stop()
    $Form.close()
})

#Action lorsque la croix rouge de l'interface est séléctionnée
$Form.add_closing({
    $timer.stop()
    $Form.close()
})

#Affichage du messagebox des informations sur l'editeur depuis le menu contextuel "A propos de"
$menuAProproDe.Add_Click({
    [System.Windows.Forms.MessageBox]::Show($msgAProposDe,"À propos de PtitSyno37",0,64)
})

#Action du timer qui est executé toutes les secondes
$timer.add_tick({
    [int]$tempCompteur = ($numCompteur.Items[-1])
    $tempCompteur++
    
    $numCompteur.Items.clear()
    $convHeures.Items.clear()
    $convMinutes.Items.clear()
    $convSecondes.Items.clear()

    $heures = [math]::Truncate(($tempCompteur / 3600))  #Récupérération des heures sans virgule
    $minutes = [math]::Truncate(($tempCompteur / 60)%60)   #Récupérération des minutes sans virgule 

    $convHeures.AddText($heures)
    $convMinutes.AddText($minutes)
    $convSecondes.AddText($tempCompteur % 60)
    $numCompteur.AddText($tempCompteur)
})

$Form.ShowDialog() | out-null

#endregion